// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/*
EPK Farm
*/
contract EPKFarmV2 is Ownable {
    IERC20 private constant EPK_LP_TOKEN =
        IERC20(address(0x5E67A806c6341b9A5235C07009ACD56D964dBA21));
    IERC20 private constant EPK_TOKEN =
        IERC20(address(0xCFd5FF9C405b04aD553ebaee06839fafDbF8FE5D));
    struct FarmInfo {
        uint256 lpBalance;
        uint256 totalEPK;
        uint256 epkBalance;
        uint256 rewardPerBlock;
        uint256 startBlock;
        uint256 endBlock;
    }

    struct UserStack {
        uint256 lpBalance;
        uint256 withdrawable;
        uint256 lastRewardBlock;
        uint256 stackBlock;
    }

    FarmInfo public farmInfo;
    mapping(address => UserStack) private userStacks;
    address[] private userList;

    constructor() {
        farmInfo = FarmInfo({
            lpBalance: 0,
            totalEPK: 0,
            epkBalance: 0,
            rewardPerBlock: 0,
            startBlock: 0,
            endBlock: 0
        });
    }

    /*
     * harvest all user's reward
     * return EPK balance to owner
     * destroy contract
     */
    function destroy() public onlyOwner {
        _harvestAll();
        for (uint256 i = 0; i < userList.length; i++) {
            address _user = userList[i];
            UserStack storage _stack = userStacks[_user];
            if (_stack.lpBalance > 0) {
                SafeERC20.safeTransfer(EPK_LP_TOKEN, _user, _stack.lpBalance);
                farmInfo.lpBalance = SafeMath.sub(
                    farmInfo.lpBalance,
                    _stack.lpBalance
                );
                _stack.lpBalance = 0;
            }
            if (_stack.withdrawable > 0) {
                SafeERC20.safeTransfer(EPK_TOKEN, _user, _stack.withdrawable);
                farmInfo.epkBalance = SafeMath.sub(
                    farmInfo.epkBalance,
                    _stack.withdrawable
                );
                _stack.withdrawable = 0;
            }
        }
        if (farmInfo.lpBalance > 0) {
            SafeERC20.safeTransfer(
                EPK_LP_TOKEN,
                msg.sender,
                farmInfo.lpBalance
            );
            farmInfo.lpBalance = 0;
        }
        if (farmInfo.epkBalance > 0) {
            SafeERC20.safeTransfer(EPK_TOKEN, msg.sender, farmInfo.epkBalance);
            farmInfo.epkBalance = 0;
        }
    }

    /*
     * Append EPK to liquidity Farm Pool
     * _amount:        EPK amount
     * _endBlock:      block number end calculating reward
     */
    function appendEPK(uint256 _amount, uint256 _endBlock) public onlyOwner {
        require(_amount > 0, "amount error");
        uint256 _startBlock = block.number;
        if (_endBlock <= _startBlock) {
            revert("start or end block number error");
        }
        SafeERC20.safeTransferFrom(
            EPK_TOKEN,
            msg.sender,
            address(this),
            _amount
        );
        _harvestAll();
        uint256 balance = farmInfo.epkBalance;
        if (balance < 0) {
            balance = 0;
        }
        balance += _amount;
        farmInfo.epkBalance = balance;
        farmInfo.startBlock = _startBlock;
        farmInfo.endBlock = _endBlock;
        farmInfo.rewardPerBlock = balance / (_endBlock - _startBlock);
        farmInfo.totalEPK += _amount;
    }

    /*
     * stack EPK-USDT LPToken into contract for farming
     * _amount:        EPK LPToken amount
     */
    function stack(uint256 _amount) public payable {
        require(_amount > 0, "amount error");
        _harvestReward(msg.sender);
        SafeERC20.safeTransferFrom(
            EPK_LP_TOKEN,
            msg.sender,
            address(this),
            _amount
        );
        _harvestAll();
        _pushUserIndex(msg.sender);
        UserStack storage _stack = userStacks[msg.sender];
        _stack.lpBalance = SafeMath.add(_stack.lpBalance, _amount);
        _stack.stackBlock = block.number;
        farmInfo.lpBalance = SafeMath.add(farmInfo.lpBalance, _amount);
    }

    /*
     * retrieve EPK LPToken ,harvest EPK reward immediately
     */
    function retrieve() public {
        _harvestAll();
        UserStack storage _stack = userStacks[msg.sender];
        require(_stack.lpBalance > 0, "no lptoken");
        _harvestAll();
        SafeERC20.safeTransfer(EPK_LP_TOKEN, msg.sender, _stack.lpBalance);
        farmInfo.lpBalance = SafeMath.sub(farmInfo.lpBalance, _stack.lpBalance);
        _stack.lpBalance = 0;
        if (_stack.withdrawable > 0) {
            SafeERC20.safeTransfer(EPK_TOKEN, msg.sender, _stack.withdrawable);
            _stack.withdrawable = 0;
        }
    }

    /*
     * Harvest EPK available reward
     */
    function harvest() public {
        _harvestReward(msg.sender);
        UserStack storage _stack = userStacks[msg.sender];
        require(_stack.withdrawable > 0, "out of balance");
        SafeERC20.safeTransfer(EPK_TOKEN, msg.sender, _stack.withdrawable);
        _stack.withdrawable = 0;
    }

    /*
     * Get available reward balance
     * _addr:        query address
     * returns "available balance"
     */
    function rewardBalance(address _addr) public view returns (uint256) {
        UserStack memory _stack = userStacks[_addr];
        uint256 _rewardPreBlock = SafeMath.div(
            SafeMath.mul(farmInfo.rewardPerBlock, _stack.lpBalance),
            farmInfo.lpBalance
        );
        uint256 _start = _stack.lastRewardBlock;
        if (_start < farmInfo.startBlock) {
            _start = farmInfo.startBlock;
        }
        uint256 _reward = SafeMath.mul(
            _rewardPreBlock,
            (block.number - _start)
        );
        return (_reward + _stack.withdrawable);
    }

    /*
     * Get EPK Farm infomations
     * returns "farm infomations"
     */
    function getFarmInfo() public view returns (FarmInfo memory _info) {
        return farmInfo;
    }

    function _harvestReward(address _user) internal {
        UserStack storage _stack = userStacks[_user];
        if (_stack.lpBalance <= 0) {
            return;
        }
        uint256 _start = _stack.lastRewardBlock;
        if (_start < farmInfo.startBlock) {
            _start = farmInfo.startBlock;
        }
        uint256 _end = block.number;
        if (_end > farmInfo.endBlock) {
            _end = farmInfo.endBlock;
        }
        uint256 _rewardPreBlock = SafeMath.div(
            SafeMath.mul(farmInfo.rewardPerBlock, _stack.lpBalance),
            farmInfo.lpBalance
        );
        uint256 _reward = SafeMath.mul(_rewardPreBlock, (_end - _start));
        _stack.withdrawable = SafeMath.add(_stack.withdrawable, _reward);
        _stack.lastRewardBlock = _end;
        farmInfo.epkBalance = SafeMath.sub(farmInfo.epkBalance, _reward);
    }

    function _harvestAll() internal {
        for (uint256 i = 0; i < userList.length; i++) {
            _harvestReward(userList[i]);
        }
    }

    function _pushUserIndex(address _addr) internal {
        for (uint256 i = 0; i < userList.length; i++) {
            if (userList[i] == _addr) {
                return;
            }
        }
        userList.push(_addr);
    }
}
